use stylist::yew::styled_component;
use yew::prelude::*;

const LOGO_BASE64_URL: &str = "data:image/png;base64,";

#[styled_component]
pub fn Portal() -> HtmlResult {
    Ok(html! {
        <div class={css!("
            position: relative;
            width: 100%;
            height: 100%;

            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
        ")}>
            <img
                src={LOGO_BASE64_URL}
                class={css!("
                    user-select: none;
                ")}
            />

            <h1 class={css!("
                margin: 1rem;
                user-select: none;
            ")}>
                {"Building on progress"}
            </h1>

            <a href={"https://github.com/celestia-island"}>
                {"Github Homepage"}
            </a>
        </div>
    })
}
